import styles from "./ErrorModal.module.css";
function ErrorModal(props) {
	return (
		<div>
			<header className={styles.header}>
				<h2>{props.title}</h2>
			</header>
			<body className={styles.content}>{props.message}</body>
			<footer className={styles.actions}>
				<button>Okay</button>
			</footer>
		</div>
	);
}

export default ErrorModal;
