// import "bootstrap";
// import "bootstrap/dist/css/bootstrap.min.css";
import { useState } from "react";
import Expenses from "./components/Expenses/Expenses";
import NewExpense from "./components/NewExpense/NewExpense";
function App() {
	const [filterYear, setFilterYear] = useState("2021");
	const expenses = [
		{
			id: "e1",
			title: "Toilet Paper",
			amount: 94.12,
			date: new Date(2020, 7, 14),
		},
		{ id: "e2", title: "New TV", amount: 799.49, date: new Date(2021, 2, 12) },
		{
			id: "e3",
			title: "Car Insurance",
			amount: 294.67,
			date: new Date(2021, 2, 28),
		},
		{
			id: "e4",
			title: "New Desk (Wooden)",
			amount: 450,
			date: new Date(2021, 5, 12),
		},
	];
	function addExpenseHandler(expense) {
		console.log(expenses);
		console.log(expense);
	}

	function printSelectedYear(year) {
		setFilterYear((prevfilterYear) => {
			return year;
		});
		console.log(filterYear);
	}
	return (
		<div>
			{/* onAddExpense points to addExpenseHandler, which defines what's to be done in order to 
			add the user-inputted expense data */}
			<NewExpense onAddExpense={addExpenseHandler} />
			<Expenses expenses={expenses} onLiftSelectedYear={printSelectedYear} />
		</div>
	);
}

export default App;
