import styles from "./MealItem.module.css";
function MealItem(props) {
	const price = `$${props.price}`;
	return (
		<div>
			<div className={styles.meal}>
				<h3>{props.name}</h3>
				<div>{props.description}</div>
				<div>{price}</div>
			</div>
		</div>
	);
}
export default MealItem;
