import Header from "./components/Layout/Header";
import Meals from "./components/Meals/Meals";
import { useState } from "react";
function App() {
	const [cartModalStatus, setCartModalStatus] = useState(false);
	function cartModalStatusHandler(status) {
		setCartModalStatus(status);
	}
	function closeHandler() {
		setCartModalStatus((prev) => !prev);
	}
	return (
		<>
			<Header cartModalStatus={cartModalStatusHandler} />
			<main>
				<Meals cartModalStatus={cartModalStatus} onClose={closeHandler} />
			</main>
		</>
	);
}

export default App;
