function Modal(props) {
  function finalizeDelete(text) {
    props.onClick();
    props.removeTodo(props.props.text);
  }
  function doNotDelete() {
    props.onClick();
  }
  return (
    <div className="modal">
      <p>Really want to delete?</p>
      <button className="btn" removeTodo={finalizeDelete}>
        Yes
      </button>
      <button className="btn btn--alt" onClick={doNotDelete}>
        No
      </button>
    </div>
  );
}

export default Modal;
